const axios = require('axios'); const qs = require('querystring'); const cheerio = require('cheerio'); const ytmp3 = async (link) => { const data = { url: link, format: 'mp3', lang: 'en' }; const req = await axios.post('https://s64.notube.net/recover_weight.php', qs.stringify(data)); const res = await axios.get('https://notube.net/en/download?token=' + req.data.token); const $ = cheerio.load(res.data); return { title: $('#breadcrumbs-section h2').text(), download: $('#breadcrumbs-section #downloadButton').attr('href'), }; }; module.exports = ytmp3;